package com.jogiyo.vo;

public class NoticeSelectDto {
	String notice_no;
	String notice_title;
	String notice_contents;	
	String n_date;
	public String getNotice_no() {
		return notice_no;
	}
	public NoticeSelectDto() {
		super();
		// TODO Auto-generated constructor stub
	}
	public NoticeSelectDto(String notice_no, String notice_title, String notice_contents, String n_date) {
		super();
		this.notice_no = notice_no;
		this.notice_title = notice_title;
		this.notice_contents = notice_contents;
		this.n_date = n_date;
	}
	@Override
	public String toString() {
		return "NoticeSelectDto [notice_no=" + notice_no + ", notice_title=" + notice_title + ", notice_contents="
				+ notice_contents + ", n_date=" + n_date + "]";
	}
	public void setNotice_no(String notice_no) {
		this.notice_no = notice_no;
	}
	public String getNotice_title() {
		return notice_title;
	}
	public void setNotice_title(String notice_title) {
		this.notice_title = notice_title;
	}
	public String getNotice_contents() {
		return notice_contents;
	}
	public void setNotice_contents(String notice_contents) {
		this.notice_contents = notice_contents;
	}
	public String getN_date() {
		return n_date;
	}
	public void setN_date(String n_date) {
		this.n_date = n_date;
	}
}
